import React from "react";

const Button = ({ children, onClick }) => {
  return (
    <button onClick={onClick}>
      <span>{children}</span>

      <style jsx>
        {`
          button {
            border-radius: 70px;
            display: block;
            font-family: 24px;
            border: 1px solid darkblue;
            background: none;
            position: relative;
            width: 100%;
            padding: 0;
            margin-bottom: 20px;
          }

          button::before {
            content: "";
            display: block;
            padding-bottom: 100%;
            padding-bottom: 30px;
          }

          button:focus,
          button:hover {
            outline: none;
            border-color: black;
          }

          span {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            display: flex;
            justify-content: space-around;
            align-items: center;
          }
        `}
      </style>
    </button>
  );
};

export default Button;
