import React from "react";

const Header = ({ title, addPlayer }) => {
  const handleAddPlayer = () => {
    const name = prompt("Enter player name");
    addPlayer(name);
  };

  return (
    <header>
      <button>Edit</button>
      <span>{title}</span>
      <button onClick={handleAddPlayer}>+</button>

      <style jsx>
        {`
          header {
            display: flex;
            justify-content: space-between;
          }
        `}
      </style>
    </header>
  );
};

export default Header;
